import Button from 'react-bootstrap/Button';
import {useRef, useState} from "react";

function App() {
    const [elements, setElements] = useState([]);
    const [inputValue, setInputValue] = useState("");
    const input = useRef();

    const new_p = () => {
        setInputValue("");
        setElements([...elements, input.value])
    }
    return (
        <div className="App">
            <header className="App-header"> Приложение
            </header>
            <input ref={input}/>
            <Button onClick={new_p}/>
            {elements ? elements.map(e =>
                <p>e</p>
            ) : ""}
        </div>
    );
}

export default App;
