import React, {useRef} from 'react';
import {Form} from "react-bootstrap";
import Button from "react-bootstrap/Button";
import axios from "axios";
import {post} from "./functions/request";
import {login} from "./functions/login";

const Reg = (props) => {

    const h_name = useRef();
    const h_id = useRef();
    const password = useRef();
    const repPassword = useRef();

    const authorisation = () =>{
        let low = 0, upper = 0, num = 0;
        for (const ch of password.current.value) {
            if (ch.toLowerCase() === ch && isNaN(Number(ch))) {
                low++;
            }
            if (ch.toUpperCase() === ch && isNaN(Number(ch))) {
                upper++;
            }
            if (!isNaN(Number(ch))) {
                num++;
            }
        }
        if (!low) {
            alert("Пароль должен содержать хотя бы одну прописную букву.");
            return;
        } else if (!upper) {
            alert("Пароль должен содержать хотя бы одну заглавную букву.");
            return;
        } else if (!num) {
            alert("Пароль должен содержать хотя бы одну цифру.");
            return;
        } else if (password.length < 10) {
            alert("Пароль должен содержать минимум 10 символов");
            return;
        }

        if (password.current.value === repPassword.current.value) {
            const name = h_name.current.value[0].toUpperCase() + h_name.current.value.slice(1);
            const id = h_id.current.value;

            post( "reg", {id, name, password}, (response) => {
                if (response === "reg") {
                    alert("Такой пользователь уже зарегистрирован");
                } else {
                    login(id, password, name);
                }
            })

        } else {
            alert("Пароли не совпадают");
        }
    }
    return (
        <div className={"registration_page"}>
            <Button className={"button_reg_auth"} onClick={props.toAuthorisation} size="sm"
                    variant="danger">Authorisation</Button>

            <Form.Group className="mb-3" controlId="ID_control_reg">
                <Form.Label>ID</Form.Label>
                <Form.Control type="text" placeholder="@000000" ref={h_id}/>
            </Form.Group>

            <Form.Group className="mb-3" controlId="name_control_reg">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" placeholder="Kitty" ref={h_name}/>
            </Form.Group>

            <Form.Group className="mb-3" controlId="password_control_reg">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" ref={password}/>
            </Form.Group>

            <Form.Group className="mb-3" controlId="password_repeat_control_reg">
                <Form.Label>Repeat password</Form.Label>
                <Form.Control type="password" placeholder="Password" ref={repPassword}/>
            </Form.Group>

            <Button onClick={props.authorisation} size="sm" variant="secondary">Go</Button>
        </div>
    );
};

export default Reg;
