import Interface from "./components/Interface";
import "./style.css"
import {useState} from "react";
import Reg from "./components/Reg";
import Auth from "./components/Auth";

function App() {
    const REG = "reg";
    const AUTH = "auth";
    const MAILS = "mails";

    const [page, setPage] = useState(REG);

    const authorisation = () => {
        setPage(MAILS)
    }

    const toRegistration = () => {
        setPage(MAILS)
    }

    const toAuthorisation = () => {
        setPage(AUTH)
    }

    if (localStorage.getItem("password") && localStorage.getItem("id")) {
        authorisation();
    }

    return (
            <div className={"container"}>
                {
                    () => {
                        switch (page) {
                            case AUTH:
                                return <Auth toRegistration={toRegistration} authorisation={authorisation}/>

                            case MAILS:
                                return <Interface toAuthorisation={toAuthorisation}/>

                            case REG:
                            default:
                                return <Reg toAuthorisation={toAuthorisation} authorisation={authorisation}/>
                        }
                    }
                }
            </div>

    );
}

export default App;
