import React, {useState} from 'react';
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap"
import Mails from "./Mails";

const UnderMenu = () => {
    const INCOMING = "Incoming";
    const OUTGOING = "Outgoing";

    const [mailsType, setMailsType] = useState(INCOMING);

    const setIncoming = () => {
        setMailsType(INCOMING);
    }
    const setOutgoing = () => {
        setMailsType(OUTGOING);
    }

    return (
        <>
            <Card.Title>
                <div className={"under-menu"}>
                    <Button variant={`${mailsType === INCOMING ? "success" : "light"}`} onClick={setIncoming}
                            size="sm">Incoming</Button>
                    <Button variant={`${mailsType === OUTGOING ? "success" : "light"}`} onClick={setOutgoing}
                            size="sm">Outgoing</Button>
                </div>
            </Card.Title>
            <Mails/>
        </>

    );
};

export default UnderMenu;
