import React from 'react';
import {Modal} from "react-bootstrap";
import {Form} from "react-bootstrap";
import Button from "react-bootstrap/Button";

const NewMessage = (props) => {
    const sendMessage = () => {

    }
    const handleClose = () => props.setShow(false);

    return (
        <>
            <Modal show={props.show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>New message</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group className="mb-3" controlId="ID_control">
                        <Form.Label>ID</Form.Label>
                        <Form.Control type="text" placeholder="Enter ID" />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="title_control">
                        <Form.Label>Title</Form.Label>
                        <Form.Control type="text" placeholder="Title" />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="text_control">
                        <Form.Label>Text</Form.Label>
                        <Form.Control as="textarea" placeholder="Your message..." />
                    </Form.Group>

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" size="sm" onClick={sendMessage}>
                        Send
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
};

export default NewMessage;
