import React, {useEffect, useState} from 'react';
import {post} from "./functions/Request";
import {Card} from "react-bootstrap";
import CardHeader from "react-bootstrap/CardHeader";
import {getTime} from "./functions/GetTime";
import Button from "react-bootstrap/Button";

const Mails = (props) => {

    const [mails, setMails] = useState([]);
    const type = props.mailsType;

    useEffect(() => {
        post("incoming_messages", {id: localStorage.getItem("id")}, (response) => {
            setMails(response);
        });
    }, [type, mails])

    return (<div>
        {mails.map(mail => {
            return (
                <Card>
                    <CardHeader className={!mail.check ? "text-bg-dark" : ""}>
                        <div className={"sender-block"}>
                            <span className={"from-span"}>
                                from:
                            </span>
                            <div className={"sender-of-message"}>
                                {mail.sender}
                            </div>
                            <div className={"time-of-message"}>
                                {getTime(mail.time)}
                            </div>
                        </div>
                        <div className={"buttons"}>

                        </div>
                    </CardHeader>
                    <Card.Body>
                        <span className={"title-span"}>
                            Title:
                        </span>
                        <div className={"title-of-message"}>
                            {mail.title}
                        </div>
                        {
                            mail.check ?
                                <span className={"text-of-message"}>
                                    {mail.text}
                                </span> :
                                <Button variant="secondary" onClick={
                                    () => {
                                        mail.check = true;
                                        post( "read", {ui:mail.ui, id: mail.recipient}, () => {
                                        });
                                    }
                                }>
                                    Read
                                </Button>
                        }
                    </Card.Body>
                </Card>)
        })}
    </div>);
};

export default Mails;
