import React from 'react';
import Button from "react-bootstrap/Button";

const Menu = (props) => {
    const handleOpen = () => props.setShow(true);

    return (
        <>
            <div className={"card-header-title"}>Cooperative Email</div>
            <span id={"id-in-menu"}>{localStorage.getItem("id")}</span>
            <div className={"menu-buttons"}>
                <Button variant="secondary" size="sm" onClick={handleOpen}>New</Button>{' '}
                <Button variant="secondary" size="sm">Delete All</Button>{' '}
                <Button variant="secondary" size="sm" onClick={props.toAuthorisation}>Exit</Button>{' '}
            </div>
        </>

    );
};

export default Menu;
