import axios from "axios";

const post = (path, data, fun) => {
    axios.post(`https://localhost:80/${path}`, data)
        .then(res=>{
            alert(re.data)
            fun(JSON.parse(res.data))
        })
}

const get = (path, fun) => {
    axios.post(`https://localhost:80/${path}`)
        .then(res=>{
            fun(JSON.parse(res.data))
        })
}

const del = (path, data, fun) => {
    axios.delete(`https://localhost:80/${path}`, data)
        .then(res=>{
            fun(JSON.parse(res.data))
        })
}

const put = (path, data, fun) => {
    axios.put(`https://localhost:80/${path}`, data)
        .then(res=>{
            fun(JSON.parse(res.data))
        })
}

export {post, get, del,put};
