import React from 'react';
import {Modal} from "react-bootstrap";
import {Form} from "react-bootstrap";
import Button from "react-bootstrap/Button";
import {put} from "./functions/Request";
import {useRef} from "@types/react";

const NewMessage = (props) => {
    const text_message = useRef();
    const recipients_for_message = useRef();
    const title_message = useRef();
    const sendMessage = () => {
        let recipients = document.getElementById("recipient-name").value.split(",");

        for (let i = 0; i < recipients.length; i++) {
            recipients[i] = recipients[i].trim();
            if (recipients[i].length !== 7 || recipients[i][0] !== '@' || isNaN(Number(recipients[i].slice(1)))) {
                alert("Ошибка в списке получателей:  " + recipients[i]);
                return;
            }
        }

        const title = document.getElementById("message-title").value;
        if (!title.trim()) {
            alert("Укажите заголовок письма.");
            document.getElementById("message-title").focus();
            return;
        }
        const text = document.getElementById("message-text").value;
        if (!text.trim()) {
            alert("Добавьте текст письма.");
            document.getElementById("message-text").focus();
            return;
        }

        const id = localStorage.getItem("id");
        const now = new Date();
        let [day, mon, year, hour, min, get] = [now.getDate() + 1, now.getMonth() + 1, now.getFullYear(), now.getHours(), now.getMinutes(), now.getTime()];
        String(day).length === 1 && (day = "0" + day);
        String(mon).length === 1 && (mon = "0" + mon);
        String(hour).length === 1 && (hour = "0" + hour);
        String(min).length === 1 && (min = "0" + min);

        const time = {day, mon, year, hour, min,get};


        recipients.forEach(recipient => {
            put(`new_message`, {id, recipient, time, title, text}, (response) => {
                if (response === "User 404") {
                    alert(`Такого пользователя не найдено: ${recipient}`);
                } else {
                    document.getElementById("close_modal").click();
                    document.getElementById("incoming_messages");
                    alert(`Письмо для ${recipient} отправлено`);
                }
            })
        })
    }
    const handleClose = () => props.setShow(false);

    return (
        <>
            <Modal show={props.show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>New message</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form.Group className="mb-3" controlId="ID_control">
                        <Form.Label>ID:</Form.Label>
                        <Form.Control type="text" placeholder="Enter ID" ref={recipients_for_message}/>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="title_control">
                        <Form.Label>Title:</Form.Label>
                        <Form.Control type="text" placeholder="Title" ref={title_message}/>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="text_control">
                        <Form.Label>Message:</Form.Label>
                        <Form.Control as="textarea" placeholder="Your message..." ref={text_message} />
                    </Form.Group>

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" size="sm" onClick={sendMessage}>
                        Send
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
};

export default NewMessage;
