import React, {useState} from 'react';
import Button from "react-bootstrap/Button";

const UnderMenu = () => {
    const INCOMING = "Incoming";
    const OUTGOING = "Outgoing";

    const [mailsType, setMailsType] = useState(INCOMING);

    const setIncoming = () =>{
            setMailsType(INCOMING);
    }
    const setOutgoing = () =>{
        setMailsType(OUTGOING);
    }
    
    return (
        <div className={"under-menu"}>
            <Button className={`btn ${mailsType === INCOMING ? "btn-success" : "btn-light"}`} onClick={setIncoming}>Incoming</Button>
            <Button className={`btn ${mailsType === OUTGOING ? "btn-success" : "btn-light"}`} onClick={setOutgoing}>Outgoing</Button>
        </div>
    );
};

export default UnderMenu;
