import Button from 'react-bootstrap/Button';
import {useRef, useState} from "react";

function App() {
    const [elements, setElements] = useState([]);
    const input = useRef();

    const new_p = () => {
        setElements([...elements, input.current.value])
        input.current.value = "";
    }
    return (
        <div className="App">
            <header className="App-header"> Приложение
            </header>
            <input ref={input} className={"form-control"}/>
            <Button onClick={new_p}>Добавить</Button>
            {elements ? elements.map(e =>
                <p>{e}</p>
            ) : ""}
        </div>
    );
}

export default App;
