import Interface from "./components/Interface";
import "./style.css"
import {useState} from "react";

function App() {
    const REG = "reg";
    const AUTH = "auth";
    const MAILS = "mails";

    const [page, setPage] = useState(REG);

    const authorisation = () => {
        setPage(MAILS)
    }

    const toRegistration = () => {
        setPage(MAILS)
    }

    const toAuthorisation = () => {
        setPage(AUTH)
    }

    if (localStorage.getItem("password") && localStorage.getItem("id")) {
        authorisation();
    }

    return (
        <>
            <div className={"container"}>
                {
                    () => {
                        switch (page) {
                            case AUTH:
                                <Auth toRegistration={toRegistration} authorisation={authorisation}/>
                                break;
                            case MAILS:
                                <Interface toAuthorisation={toAuthorisation}/>
                                break;
                            case REG:
                            default:
                                <Reg toAuthorisation={toAuthorisation} authorisation={authorisation}/>
                                break;
                        }
                    }
                }
            </div>
        </>

    );
}

export default App;
